function getChunkFileName(thunderState, chunkName) {
  const chunkInfo = thunderState.chunksMap[chunkName];
  let filePattern = '';
  if (chunkInfo.type === 'entry') {
    filePattern = thunderState.outputOptions.filename;
  } else if (chunkInfo.type === 'chunk') {
    filePattern = thunderState.outputOptions.chunkFilename;
  } else {
    throw new Error(`Unknown chunk type ${chunkInfo.type}`)
  }
  return filePattern
  .replace('[name]', chunkName)
  .replace('[id]', chunkName)
  .replace('[chunkhash]', chunkInfo.hash)
  .replace('[hash]', chunkInfo.hash);
}

module.exports = {
  getChunkFileName,
};