import Promise from '../Promise'
import Thunder from '../thunder'
import Logger from '../logger'
import Store from '../store/indexed-store'
import starkPlugin from '@mfe/thunder-stark-plugin/client'
import serviceWorkerPlugin from '../service-worker'

// 暴露 Promise polyfill
window.Promise = Promise

Thunder
  .use(Logger)
  .use(Store)
  .use(serviceWorkerPlugin)
  .use(() => starkPlugin(Thunder))

export default Thunder
