import { isBrowser } from '../util'

const supportPerformance = isBrowser && 'performance' in window && 'now' in performance

export function getRandomInt (min = 0, max = 9) {
  return Math.floor(Math.random() * (max - min + 1)) + min
}

export function fakeId () {
  let i = 16
  let id = ''
  while (i--) {
    id += '' + getRandomInt()
  }
  return id
}

export function timeNow () {
  if (supportPerformance) return performance.now()
  return Date.now()
}
