const getThunderScript = require('./thunder-script')
const getThunderServiceWorker = require('./thunder-service-worker')
const assert = require('assert')

function getThunder (thunderState, forceUpdate) {
  assert(
    thunderState instanceof Object,
    'thunderState should be object, but got ' + typeof thunderState + ' : ' + thunderState
  )
  return {
    client: getThunderScript(thunderState, forceUpdate),
    serviceWorker: getThunderServiceWorker(thunderState, forceUpdate)
  }
}

module.exports = getThunder
